import * as React from 'react'
import './fnt_sze.css'

export default function Font(props:any){

let color_a,color_b,color_c,color_d; 

const [Fntsze, SetFntsze] = React.useState(20)

React.useEffect(()=>{props.fntSlct(Fntsze);},[Fntsze])

const fntSze_a=()=>{
    SetFntsze(20)
 }
const fntSze_b=()=>{
    SetFntsze(22)
}
const fntSze_c=()=>{
    SetFntsze(24)
    }
const fntSze_d=()=>{
    SetFntsze(26)
   }

if(Fntsze===20){
    color_a='red'
} else if(Fntsze===22){
    color_b='red'
} else if (Fntsze===24){
    color_c='red'
} else if(Fntsze===26){
    color_d='red'
}


return (
<div>

    <figure className = 'fnt_slct'>

        <img  src = {require('../images/font.svg')} alt = 'Adjust font size'/>

        <figcaption style={{display:props.LangEN}}><b>Font size</b>
        <figcaption style={{display:props.LangDE}}><b>Schriftgröße</b>

         <div className='fnt_list' >
            <h3 onClick = {fntSze_a} style={{color:color_a}}>20</h3>
            <h3 onClick = {fntSze_b} style={{color:color_b}}>22</h3>
            <h3 onClick = {fntSze_c} style={{color:color_c}}>24</h3>
            <h3 onClick = {fntSze_d} style={{color:color_d}}>26</h3>
         </div>

        </figcaption>
        </figcaption>

    </figure>
</div>
)
}
