import * as React from 'react';
import Title from './components/title_bar';
import Navbar from './components/nav_bar';
import './pageOne.css';
import {useSelector} from 'react-redux';
import {langStatus} from './components/redux';
import {useState,useEffect} from 'react';
import axios from 'axios';

export default function Home() {

  let varENG:string,varDEU:string

  const slctdLang = useSelector((state:langStatus)=>state.slctdLang.lang)

 if (slctdLang === 'ENG'){
    varENG = 'block'
    varDEU='none'       
  } else {
    varENG = 'none'
    varDEU='block'    
 }

 const [State_newsUpdates,setState_newsUpdates] = useState([])
 const [State_eventsCalender,setState_eventsCalender] = useState([])

 useEffect(() => {      
  fetchData();    
   },[])

 function fetchData(){
    axios.all([ 
    axios.get('newsUpdate_api'),
    axios.get('eventsCalender_api')
    ])   
   .then(retrieve => {setState_newsUpdates(retrieve[0].data);
                      setState_eventsCalender(retrieve[1].data)

      })
   }

  return(

  <div> 

    <Title/>

    <Navbar pg_Home = "4px solid red"/>

    <div className = 'newsbox_a'>
      <p style={{display:varENG}}>News & Updates</p>
      <p style={{display:varDEU}}>Nachrichten & Updates</p>
    </div> 

    <div className='newsbox_b'>
      {State_newsUpdates.map((data:any) => (
        <div>        
          <p style={{ display: varENG }}>{data.newsUpdates_EN}</p>
          <p style={{ display: varDEU }}>{data.newsUpdates_DE}</p>
        </div>
      ))}      
    </div>

   <div className = 'eventbox'>

      <p style={{display:varENG}}>Upcoming Events</p>
      <p style={{display:varDEU}}>anstehende Veranstaltungen</p>



      <div className = 'grid_cont_event'>

        <div style={{display:varENG}}>Date</div>     
        <div style={{display:varENG}}>Time(24Hrs)</div> 
        <div style={{display:varENG}}>Duration(Hrs)</div>
        <div style={{display:varENG}}>Event</div>

        <div style={{display:varDEU}}>Datum</div>     
        <div style={{display:varDEU}}>Uhrzeit(24Hrs)</div> 
        <div style={{display:varDEU}}>Dauer(Hrs)</div>
        <div style={{display:varDEU}}>Veranstaltung</div>

        {State_eventsCalender.map((data:any) => 
        <div>
        <div style={{display:varENG}}>{data.date}</div>     
        <div style={{display:varENG}}>{data.time}</div> 
        <div style={{display:varENG}}>{data.duration}</div>
        <div style={{display:varENG}}>{data.events_EN}</div>
        </div>
        )}

      </div>



   </div>

    <div className = 'photobox'>
      <p className = 'photobox_txt' style={{display:varENG}}>Recent events</p>
      <p className = 'photobox_txt' style={{display:varDEU}}>Neueste Veranstaltungen</p>

      <figure >
        <img className = 'photobox_img_a'  src = {require('./images/cont_a.jpg')} alt='Pancake Tuesday Celebrations' />
        <figcaption className='img_a_capt' style={{display:varENG}}>Pancake Tuesday...<b>Read more....</b></figcaption>
        <figcaption className='img_a_capt' style={{display:varDEU}}>Veilchendienstag!....<b>lesen Sie mehr.......</b></figcaption>
      </figure>

      <figure >
        <img className = 'photobox_img_b'  src = {require('./images/cont_b.jpg')} alt='New Year Celebrations' />
        <figcaption className='img_b_capt' style={{display:varENG}}>Welcoming 2020..<b>Know more..</b></figcaption>
        <figcaption className='img_b_capt' style={{display:varDEU}}>Ein gelungener Jahresauftakt..<b>Mehr wissen..</b></figcaption>
      </figure>

      <figure >
        <img className = 'photobox_img_c'  src = {require('./images/cont_c.jpg')} alt='New Year Celebrations' />
        <figcaption className='img_c_capt' style={{display:varENG}}>Ghent, Belgium..<b>Explore..</b></figcaption>
        <figcaption className='img_c_capt' style={{display:varDEU}}>Ghent, Belgium..<b>Erkunden Sie..</b></figcaption>
      </figure>

      <figure >
        <img className = 'photobox_img_d'  src = {require('./images/cont_d.jpg')} alt='New Year Celebrations' />
        <figcaption className='img_d_capt' style={{display:varENG}}>Bike safely..<b>Learn more..</b></figcaption>
        <figcaption className='img_d_capt' style={{display:varDEU}}>sicher radeln..<b>mehr Erfahren..</b></figcaption>
      </figure>

      <figure >
        <img className = 'photobox_img_e'  src = {require('./images/cont_e.jpg')} alt='New Year Celebrations' />
        <figcaption className='img_e_capt' style={{display:varENG}}>Carnival at ICJ..<b>Details..</b></figcaption>
        <figcaption className='img_e_capt' style={{display:varDEU}}>Karneval im Club..<b>mehr Einzelheiten..</b></figcaption>
      </figure>

    </div >

  </div>
  )
}
