import {createStore,combineReducers} from 'redux'

interface ISetEnglish{
    type:'SET_ENGLISH'
    payload: string
}
interface ISetDeutsch{
    type:'SET_DEUTSCH'
    payload:string
}
export type TSetLang= |ISetEnglish|ISetDeutsch

type langState = {
    lang:string
}

const InitialLang:langState={
    lang:'ENG'
}

const LangReducer = (state:langState=InitialLang,action:TSetLang)=>{
    switch(action.type){
        case 'SET_ENGLISH':
         return {...state, lang:action.payload}
        case 'SET_DEUTSCH':
         return {...state, lang:action.payload}
        default:
         return state         
    }   

}


const rootReducer = combineReducers({
    LangReducer,
})
export const store = createStore(LangReducer)

export type langStatus = ReturnType<typeof LangReducer>
